function tt = create_states_timetable(time, states)%%CREATE_STATES_TIMETABLE Criar timetable padronizado para estados%%%% Autor: Vitor Yukio - UnB/PIBIC%%%% Validaçãoif size(states, 2) ~= 6    error('Estados devem ter 6 colunas, recebido: %d', size(states, 2));endif size(states, 1) ~= length(time)    error('Dimensão incompatível: time=%d, states=%d', length(time), size(states,1));end%%%% Criar timetablett = timetable(seconds(time), ...    states(:,1), states(:,2), states(:,3), ...    states(:,4), states(:,5), states(:,6), ...    'VariableNames', {'beta', 'r', 'phi', 'p', 'phi_uf', 'phi_ur'});%%%% Metadadostt.Properties.VariableUnits = {'rad', 'rad/s', 'rad', 'rad/s', 'rad', 'rad'};tt.Properties.VariableDescriptions = {    'Sideslip angle (β)', ...    'Yaw rate (r)', ...    'Roll angle sprung mass (φ)', ...    'Roll rate (p)', ...    'Roll angle unsprung front (φ_uf)', ...    'Roll angle unsprung rear (φ_ur)'};tt.Properties.Description = '5-DOF Vehicle States (Yaw-Roll Model)';end
